import React, { useState } from 'react';

function Search({ redirect, linkToResults, setUserQuery }) {
    const [userInput, setUserInput] = useState('');
    const [zipInput, setZipInput] = useState('');

    function isNumberKey(e) {
        debugger;
        let charCode = e.which ? e.which : e.keyCode;

        if (charCode > 31 && (charCode < 48 || charCode > 57)) {
            return false;
        }

        return true;
    }

    function handleInput(e) {
        setUserInput(e.target.value);
    }

    function handleZipInput(e) {
        let zipVal = e.target.value;

        if (isNumberKey(e) && zipVal.length <= 5) {
            setZipInput(zipVal);
        }
    }

    function handleClick(e) {
        e.preventDefault();

        setUserQuery(userInput);

        if (redirect) {
            linkToResults();
        }
    }

    return (
        <div className="search-form">
            <form>
                <label>
                    <span>Search Term:</span>
                    <input type="text" placeholder="What are you looking for?" value={userInput} onChange={e => handleInput(e)} />
                </label>
                <label>
                    <span>Zip Code:</span>
                    <input type="text" pattern="^[0â€“9]$" placeholder="Zip Code" value={zipInput} onChange={e => handleZipInput(e)} />
                </label>
                <button onClick={e => handleClick(e)}>Find Non-Profit</button>
            </form>
        </div>
    );
}

export default Search;
